var myMapApp = new mapApp(false,undefined);
function initLater() {
		// this timeout is because of the bbox bug in Firefox which prevents to have access to bbox in onload scripts
	window.setTimeout("init()",100);
}
function init() {
		//tooltip styles
	var tooltipTextStyles = {"font-family":"Open Sans","fill":"dimgray","font-size":7.5};
	var tooltipRectStyles = {"fill":"white","stroke":"dimgray"};
	myMapApp.initTooltips("toolTip",tooltipTextStyles,tooltipRectStyles,10,20,3);
		//add tooltip to element
	myMapApp.addTooltip("dariah",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("dhforum",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("nestor",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("bmbf",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("textgrid",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("clarin",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("allianz",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("dfg",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("rfii",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("dini",undefined,false,false,"currentTarget",undefined);
	myMapApp.addTooltip("wr",undefined,false,false,"currentTarget",undefined);
}
function updateTooltipWithMouseCoords(evt) {
	var coords = myMapApp.calcCoord(evt);
	evt.target.setAttributeNS(attribNS,"tooltip","X: "+Math.round(coords.x)+"\\nY: "+Math.round(coords.y));
}
